/*
 *
 * Radium_Options_radio_img function
 * Changes the radio select option, and changes class on images
 *
 */function radium_radio_img_select(e,t){jQuery(this).prev('input[type="radio"]').prop("checked");jQuery(".radium-radio-img-"+t).removeClass("radium-radio-img-selected");jQuery('label[for="'+e+'"]').addClass("radium-radio-img-selected")};