/*global jQuery, document, radium_upload, formfield:true, preview:true, tb_show, window, imgurl:true, tb_remove, $relid:true*//*
This is the uploader for wordpress starting from version 3.5
*/jQuery(document).ready(function(){jQuery("img[src='']").attr("src",radium_upload.url);jQuery(".radium-opts-upload").click(function(e){var t=jQuery(this).parent(),n=jQuery(this).attr("rel-id");e.preventDefault();custom_file_frame=null;custom_file_frame=wp.media.frames.customHeader=wp.media({title:jQuery(this).data("choose"),library:{type:"image"},button:{text:jQuery(this).data("update")}});custom_file_frame.on("select",function(){var e=custom_file_frame.state().get("selection").first();jQuery(".radium-opts-screenshot",t).attr("src",e.attributes.url);jQuery("#"+n).val(e.attributes.url).trigger("change");jQuery(".radium-opts-upload",t).hide();jQuery(".radium-opts-screenshot",t).show();jQuery(".radium-opts-upload-remove",t).show()});custom_file_frame.open()});jQuery(".radium-opts-upload-remove").click(function(e){var t=jQuery(this).parent(),n=jQuery(this).attr("rel-id");e.preventDefault();jQuery("#"+n).val("");jQuery(this).prev().fadeIn("slow");jQuery(".radium-opts-screenshot",t).fadeOut("slow");jQuery(this).fadeOut("slow")})});